import React from 'react';
import {getHeaders} from './utils';

class Posts extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            posts: []
        }
        // initialization code here
        this.getPostsFromServer()
    }

    getPostsFromServer () {
        fetch('/api/posts', {
            headers: getHeaders()
        }).then(response => response.json())
        .then(data => {
            console.log(data);
            this.setState({
                posts: data
            })
        })
    }
    componentDidMount() {
        // fetch posts and then set the state...
    }

     render () {
         return (
             <div id="posts">
                    {
                        this.state.posts.map(post => {
                            console.log(post);
                            return (
                                <section 
                                    className = "post"
                                    key={'post_' + post.id}>
                                    <img src="{post.image_url}" />
                                    <p>{post.caption}</p>
                                </section>
                            )
                        })
                    }
                    </div>
            )
                }
}

export default Posts;
